<?php

/**
 * The admin-specific functionality of the plugin.
 *
 * @link       https://wordpress.org/plugins/azure-app-service-migration/
 * @since      1.0.0
 *
 * @package    Azure_app_service_migration
 * @subpackage Azure_app_service_migration/admin
 */

/**
 * The admin-specific functionality of the plugin.
 *
 * Defines the plugin name, version, and two examples hooks for how to
 * enqueue the admin-specific stylesheet and JavaScript.
 *
 * @package    Azure_app_service_migration
 * @subpackage Azure_app_service_migration/admin
 * @author     Microsoft <wordpressdev@microsoft.com>
 */
class Azure_app_service_migration_Admin
{

    /**
     * The ID of this plugin.
     *
     * @since    1.0.0
     * @access   private
     * @var      string    $plugin_name    The ID of this plugin.
     */
    private $plugin_name;

    /**
     * The version of this plugin.
     *
     * @since    1.0.0
     * @access   private
     * @var      string    $version    The current version of this plugin.
     */
    private $version;

    /**
     * Initialize the class and set its properties.
     *
     * @since    1.0.0
     * @param      string    $plugin_name       The name of this plugin.
     * @param      string    $version    The version of this plugin.
     */
    public function __construct($plugin_name, $version)
    {

        $this->plugin_name = $plugin_name;
        $this->version = $version;
    }

    /**
     * Register the stylesheets for the admin area.
     *
     * @since    1.0.0
     */
    public function enqueue_styles()
    {

        /**
         * This function is provided for demonstration purposes only.
         *
         * An instance of this class should be passed to the run() function
         * defined in Azure_app_service_migration_Loader as all of the hooks are defined
         * in that particular class.
         *
         * The Azure_app_service_migration_Loader will then create the relationship
         * between the defined hooks and the functions defined in this
         * class.
         */

        $valid_pages = array("azure_app_service_migration_menu", "azure_app_service_migration_import");
        $page = isset($_REQUEST['page']) ? $_REQUEST['page'] : "";
        if (in_array($page, $valid_pages)) {
            // adding css files in valid pages
            // Plugin Custom Css File
            wp_enqueue_style("azure_app_service_migrations_bootstrap", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/css/bootstrap.min.css', array(), $this->version, 'all');
            // Plugin Custom Datatable Css File
            wp_enqueue_style("azure_app_service_migrations_datatablebootstrap", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/css/jquery.dataTables.min.css', array(), $this->version, 'all');
            // Plugin Custom Sweet alert Css File
            wp_enqueue_style("azure_app_service_migrations_sweetalertbootstrap", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/css/sweetalert.css', array(), $this->version, 'all');
            // wp_enqueue_style( $this->plugin_name, plugin_dir_url( __FILE__ ) . 'css/azure_app_service_migration-admin.css', array(), $this->version, 'all' );
        }
    }

    /**
     * Register the JavaScript for the admin area.
     *
     * @since    1.0.0
     */
    public function enqueue_scripts()
    {
        /**
         * This function is provided for demonstration purposes only.
         *
         * An instance of this class should be passed to the run() function
         * defined in Azure_app_service_migration_Loader as all of the hooks are defined
         * in that particular class.
         *
         * The Azure_app_service_migration_Loader will then create the relationship
         * between the defined hooks and the functions defined in this
         * class.
         */
        $valid_pages = array("azure_app_service_migration_menu", "azure_app_service_migration_import");
        $page = isset($_REQUEST['page']) ? $_REQUEST['page'] : "";
        if (in_array($page, $valid_pages)) {
            wp_enqueue_script("jquery");
            // Plugin Custom bootstrap Js File
            wp_enqueue_script("azure_app_service_migrations_bootstrap_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/bootstrap.min.js', array('jquery'), $this->version, false);

            // Plugin Custom datatable Js File
            wp_enqueue_script("azure_app_service_migrations_datatablebootstrap_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/jquery.dataTables.min.js', array('jquery'), $this->version, false);

            // Plugin Custom Validation Js File
            wp_enqueue_script("azure_app_service_migrations_validationbootstrap_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/jquery.validate.min.js', array('jquery'), $this->version, false);

            // Plugin Custom Sweetalert Js File
            wp_enqueue_script("azure_app_service_migrations_sweetalertbootstrap_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/sweetalert.min.js', array('jquery'), $this->version, false);

            // Plugin Custom Fluent UI Components
            // wp_enqueue_script("azure_app_service_migrations_FluentUI_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/fluentui-web-components.js', array('jquery'), $this->version, false);

            // Ajax Custome Js file
            wp_enqueue_script("azure_app_service_migrations_ajax_js", AZURE_APP_SERVICE_MIGRATION_PLUGIN_URL . 'assets/js/ajax_jquery.js', array('jquery'), $this->version, false);

            wp_enqueue_script($this->plugin_name, plugin_dir_url(__FILE__) . 'js/azure_app_service_migration-admin.js', array('jquery'), $this->version, false);

            wp_localize_script($this->plugin_name, "azure_app_service_migration", array(
                "name" => "Azure App Service Migration",
                "author" => "Microsoft",
                "ajaxurl" => admin_url("admin-ajax.php"),
            ));
        }
    }

    // Create menu method
    public function azure_app_service_migration_menu()
    {
        // Plugin Menu Name
        add_menu_page("Azure App Service Migration", "Azure App Service Migration", "manage_options", "azure_app_service_migration_menu", array($this, "azure_app_service_migration_export"), "data:image/svg+xml;base64,", 22);

        // Plugin Submenu Names
        add_submenu_page("azure_app_service_migration_menu", "Export", "Export", "manage_options", "azure_app_service_migration_menu", array($this, "azure_app_service_migration_export"));
        add_submenu_page("azure_app_service_migration_menu", "Import", "Import", "manage_options", "azure_app_service_migration_import", array($this, "azure_app_service_migration_import"));
        //add_submenu_page("azure_app_service_migration_menu","Backup","Backup","manage_options","azure_app_service_migration_backup", array($this,"azure_app_service_migration_backup"));
    }

    // Menu callback function
    // Export Callback Funtion
    public function azure_app_service_migration_export()
    {
        //echo "<h3>Welcome to Plugin Export</h3>";
        ob_start(); // started buffer
        include AZURE_APP_SERVICE_MIGRATION_PLUGIN_PATH . "admin/partials/tmpl_export.php"; // Include template file
        $template = ob_get_contents(); //reading content
        ob_get_clean(); // closing and cleaning buffer

        echo $template;
    }

    // Import Callback Funtion
    public function azure_app_service_migration_import()
    {
        //echo "<h3>Welcome to Plugin Export</h3>";
        ob_start(); // started buffer
        include AZURE_APP_SERVICE_MIGRATION_PLUGIN_PATH . "admin/partials/tmpl_import.php"; // Include template file
        $template = ob_get_contents(); //reading content
        ob_get_clean(); // closing and cleaning buffer
        echo $template;
    }
    // // backup Callback Funtion
    // public function azure_app_service_migration_backup(){
    //     echo "<h3>Welcome to Plugin Backup</h3>";
    // }    
}
